-- ----------------------------
-- Table structure for r_news
-- ----------------------------
DROP TABLE IF EXISTS `r_news`;
CREATE TABLE `r_news` (
  `newsid` int(11) NOT NULL auto_increment COMMENT '新闻编号',
  `cid` int(11) default NULL COMMENT '新闻分类',
  `newstitle` varchar(80) default NULL COMMENT '新闻标题',
  `summary` text COMMENT '摘要',
  `content` text COMMENT '新闻内容',
  `author` varchar(20) default '互联网' COMMENT '新闻来源',
  `newstime` datetime default NULL COMMENT '发布时间',
  `thumbnail_url` varchar(100) default NULL COMMENT '新闻图片',
  `news_url` varchar(100) default NULL,
  PRIMARY KEY  (`newsid`),
  KEY `FK_r_news_r_category_idx` (`cid`),
  CONSTRAINT `FK_r_news_r_category` FOREIGN KEY (`cid`) REFERENCES `r_category` (`cid`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE=InnoDB AUTO_INCREMENT=46 DEFAULT CHARSET=utf8;

